css:
body {
  font-size: 12px;
  color: #a8a8a8;
  background-color: #0d0d0d;
}
.btn {
  color: #3d3d3d;
  background-color: #f0f0f0;
}
.btn:hover,
.btn:focus {
  color: #3d3d3d;
  background-color: #ffffff;
}
.btn:active {
  color: #3d3d3d;
  background-color: #ebebeb;
}
.btn.selected {
  color: #3d3d3d;
  background-color: #ffffff;
}
.btn:focus,
.btn:focus:active {
  outline: none;
}
.btn.btn-primary {
  color: #ffffff;
  background-color: #8bdff4;
}
.btn.btn-primary:hover,
.btn.btn-primary:focus {
  background-color: #9ee4f5;
}
.btn.btn-primary:active {
  background-color: #78d9f2;
}
.btn.btn-primary.selected {
  background-color: #9ee4f5;
}
.btn.btn-primary.selected:focus,
.btn.btn-primary.selected:hover {
  background-color: #b0e9f7;
}
.btn.btn-info {
  color: #ffffff;
  background-color: #8bdff4;
}
.btn.btn-info:hover,
.btn.btn-info:focus {
  background-color: #9ee4f5;
}
.btn.btn-info:active {
  background-color: #78d9f2;
}
.btn.btn-info.selected {
  background-color: #9ee4f5;
}
.btn.btn-info.selected:focus,
.btn.btn-info.selected:hover {
  background-color: #b0e9f7;
}
.btn.btn-success {
  color: #ffffff;
  background-color: #c3e88d;
}
.btn.btn-success:hover,
.btn.btn-success:focus {
  background-color: #cceb9e;
}
.btn.btn-success:active {
  background-color: #bae47c;
}
.btn.btn-success.selected {
  background-color: #cceb9e;
}
.btn.btn-success.selected:focus,
.btn.btn-success.selected:hover {
  background-color: #d5efaf;
}
.btn.btn-warning {
  color: #ffffff;
  background-color: #f78a69;
}
.btn.btn-warning:hover,
.btn.btn-warning:focus {
  background-color: #f8997c;
}
.btn.btn-warning:active {
  background-color: #f67b55;
}
.btn.btn-warning.selected {
  background-color: #f8997c;
}
.btn.btn-warning.selected:focus,
.btn.btn-warning.selected:hover {
  background-color: #f9a890;
}
.btn.btn-error {
  color: #ffffff;
  background-color: #f07076;
}
.btn.btn-error:hover,
.btn.btn-error:focus {
  background-color: #f28288;
}
.btn.btn-error:active {
  background-color: #ee5d65;
}
.btn.btn-error.selected {
  background-color: #f28288;
}
.btn.btn-error.selected:focus,
.btn.btn-error.selected:hover {
  background-color: #f4959a;
}
atom-text-editor[mini] {
  padding-left: 5px;
  color: #a8a8a8;
  border-radius: 2px;
  background-color: #151515;
}
atom-text-editor[mini].is-focused {
  background-color: #080808;
}
.find-and-replace {
  border-top: 1px solid #1a1a1a;
}
.status {
  color: #a8a8a8;
}
.status-added {
  color: #c3e88d;
}
.status-ignored {
  color: #808080;
}
.status-modified {
  color: #f78a69;
}
.status-removed {
  color: #f07076;
}
.status-renamed {
  color: #8bdff4;
}
.key-binding {
  display: inline-block;
  margin-left: 1px;
  padding: 0 5px;
  line-height: 2;
  font-family: 'Helvetica Neue', Arial, sans-serif;
  font-size: .8em;
  letter-spacing: 1px;
  border-radius: 2px;
  background-color: #0d0d0d;
}
.project-root-header {
  height: 40px;
  display: flex;
  align-items: center;
}
.project-root.selected::before {
  height: 40px;
}
.list-group li {
  padding-left: 5px;
}
.list-group li.selected {
  color: #ffffff;
  background-color: #242424;
}
.list-group li.selected:before {
  display: none;
}
.list-tree .selected {
  color: #ffffff;
}
.select-list li {
  padding: 5px 10px;
}
.select-list .status {
  float: right;
}
.select-list.popover-list {
  padding: 5px;
  border-radius: 2px;
  background-color: #0d0d0d;
  box-shadow: 0 2px 8px 1px rgba(0, 0, 0, 0.3);
}
.select-list.popover-list .list-group {
  margin-top: 5px;
}
.select-list.popover-list .list-group li {
  padding-left: 5px;
}
.panel-heading {
  border-bottom: 1px solid #1a1a1a;
  background-color: #0f0f0f;
}
.inset-panel {
  background-color: #0f0f0f;
}
.inset-panel .panel-heading {
  background-color: #1a1a1a;
}
.overlay,
atom-panel.modal {
  padding: 20px;
}
.overlay.from-top,
atom-panel.modal.from-top {
  position: absolute;
  top: 40%;
  left: 50%;
  transform: translate(-50%, 0);
  margin: 0;
}
.overlay .editor.mini,
atom-panel.modal .editor.mini {
  padding: 20px;
  border-radius: 2px;
  border-bottom: 1px solid #292929;
  background-color: #0d0d0d;
  max-height: none;
}
.loading-spinner-large {
  display: block;
  width: 64px;
  height: 64px;
  background-image: url(images/octocat-spinner-128.gif);
  background-repeat: no-repeat;
  background-size: 64px 64px;
}
.loading-spinner-large.inline-block {
  display: inline-block;
}
.loading-spinner-medium {
  display: block;
  width: 48px;
  height: 48px;
  background-image: url(images/octocat-spinner-128.gif);
  background-repeat: no-repeat;
  background-size: 48px 48px;
}
.loading-spinner-medium.inline-block {
  display: inline-block;
}
.loading-spinner-small {
  display: block;
  width: 32px;
  height: 32px;
  background-image: url(images/octocat-spinner-128.gif);
  background-repeat: no-repeat;
  background-size: 32px 32px;
}
.loading-spinner-small.inline-block {
  display: inline-block;
}
.loading-spinner-tiny {
  display: block;
  width: 16px;
  height: 16px;
  background-image: url(images/octocat-spinner-128.gif);
  background-repeat: no-repeat;
  background-size: 16px 16px;
}
.loading-spinner-tiny.inline-block {
  display: inline-block;
}
.ui-site-1 {
  background-color: #0088ff;
}
.ui-site-2 {
  background-color: #20b684;
}
.ui-site-3 {
  background-color: #cc8533;
}
.ui-site-4 {
  background-color: #d831b0;
}
.ui-site-5 {
  background-color: #ebdd5b;
}
.status-bar {
  font-size: 12px;
  color: #a8a8a8;
  background-color: #141414;
  height: 50px;
}
.status-bar .flexbox-repaint-hack {
  align-items: center;
}
.tab-bar {
  background-color: #1a1a1a;
  border-bottom: 1px solid #1a1a1a;
}
.tab-bar .tab {
  color: #a8a8a8;
  height: 40px;
  line-height: 40px;
  font-size: 12px;
  border-right: 1px solid #1a1a1a;
  background-color: #1a1a1a;
}
.tab-bar .tab.active {
  color: #ffffff;
  background-color: #1a1a1a;
}
.tab-bar .tab.modified:not(:hover) .close-icon {
  top: 50%;
  right: 14px;
  margin-top: -3px;
  border-color: #8bdff4;
}
.tab-bar .tab.modified:hover .close-icon {
  color: currentColor;
}
.tab-bar .tab .close-icon:hover {
  color: #ffffff;
}
.tab-bar .tab.active .close-icon:hover {
  color: #a8a8a8;
}
.tab-bar .tab.is-dragging {
  background: #000000;
  border-color: transparent;
  opacity: .5;
}
.tab-bar .tab.is-dragging .close-icon {
  visibility: hidden;
}
.text-smaller {
  font-size: smaller;
}
.text-subtle {
  color: #808080;
}
.text-highlight {
  color: #f0f0f0;
}
.text-info {
  color: #8bdff4;
}
.text-success {
  color: #c3e88d;
}
.text-warning {
  color: #f78a69;
}
.text-error {
  color: #f07076;
}
.highlight {
  padding: 1px 4px;
  color: #f0f0f0;
  border-radius: 2px;
  background-color: #1f1f1f;
}
.highlight-info {
  padding: 1px 4px;
  color: #f0f0f0;
  border-radius: 2px;
  background-color: #8bdff4;
}
.highlight-warning {
  padding: 1px 4px;
  color: #f0f0f0;
  border-radius: 2px;
  background-color: #f78a69;
}
.highlight-error {
  padding: 1px 4px;
  color: #f0f0f0;
  border-radius: 2px;
  background-color: #f07076;
}
.highlight-success {
  padding: 1px 4px;
  color: #f0f0f0;
  border-radius: 2px;
  background-color: #c3e88d;
}
.background-message {
  font-weight: bold;
  color: #808080;
}
.tooltip .tooltip-inner {
  line-height: 1;
  padding: .75em;
  white-space: nowrap;
  max-width: none;
}
.tooltip .keystroke {
  padding: .15em .4em;
  margin: 0 -0.3em 0 0.25em;
  border-radius: 2px;
  color: #ffffff;
  background: #242424;
}
.tree-view {
  color: #a8a8a8;
  background-color: #1a1a1a;
}
.tree-view .list-item.selected {
  color: #ffffff;
}
.icon:before {
  color: #3b3b3b;
  margin-right: 10px;
}
.list-group .icon::before,
.list-tree .icon::before {
  margin-right: 10px;
}
