/*
@cyan:         #35a766;
@blue:         #5977d3;
@purple:       #8100c6;
@green:        #8fad06;
@red:          #d24d4d;
@orange:       #cd8e21;
@light-orange: #e3a92d;
*/
/* highlighting is the one intentional departure from gtk's styles */
#linter-tooltip {
  margin-top: 3px;
}
#linter-tooltip linter-message.error {
  color: #ffffff;
  background-color: #d51010;
}
#linter-tooltip linter-message.error:last-child::before {
  border-color: transparent transparent #d51010 #d51010;
}
#linter-tooltip linter-message.error a {
  color: #ffffff;
}
#linter-tooltip linter-message.warning {
  color: #ffffff;
  background-color: #f57900;
}
#linter-tooltip linter-message.warning:last-child::before {
  border-color: transparent transparent #f57900 #f57900;
}
#linter-tooltip linter-message.warning a {
  color: #ffffff;
}
#linter-tooltip linter-message.info {
  color: #ffffff;
  background-color: #346598;
}
#linter-tooltip linter-message.info:last-child::before {
  border-color: transparent transparent #346598 #346598;
}
#linter-tooltip linter-message.info a {
  color: #ffffff;
}
#linter-tooltip linter-message.info::before {
  opacity: 0;
}
#linter-tooltip linter-message:last-child::before {
  content: "";
  position: absolute;
  top: -4px;
  left: 0;
  border: 4px solid;
}
#linter-tooltip linter-message a {
  font-weight: bold;
}
#linter-tooltip linter-message p {
  margin-bottom: 0;
}
#linter-tooltip linter-message .linter-line {
  display: block;
}
#linter-tooltip linter-message .badge {
  color: #000000;
}
linter-message {
  display: block;
  padding: 2px 5px;
}
.icon.linter-icon::before {
  font-size: 14px;
}
atom-text-editor.editor .linter-row {
  /* Take up the full allowed width */
  left: 0;
  right: 0;
  /* Align the linter dot in the middle */
  display: flex;
  align-items: center;
  justify-content: center;
}
atom-text-editor.editor .linter-highlight.linter-info .region,
.linter-highlight.linter-info .region {
  background-image: linear-gradient(45deg, transparent 65%, rgba(98, 135, 173, 0.85) 80%, transparent 90%), linear-gradient(135deg, transparent 5%, rgba(98, 135, 173, 0.85) 15%, transparent 25%), linear-gradient(135deg, transparent 45%, rgba(98, 135, 173, 0.85) 55%, transparent 65%), linear-gradient(45deg, transparent 25%, rgba(98, 135, 173, 0.85) 35%, transparent 50%);
  background-size: 8px 2px;
  background-repeat: repeat-x;
  background-position: left bottom;
}
atom-text-editor.editor .linter-highlight.linter-info.linter-gutter,
.linter-highlight.linter-info.linter-gutter {
  color: rgba(98, 135, 173, 0.85);
  z-index: -1;
}
atom-text-editor.editor .linter-highlight.linter-warning .region,
.linter-highlight.linter-warning .region {
  background-image: linear-gradient(45deg, transparent 65%, rgba(243, 150, 59, 0.85) 80%, transparent 90%), linear-gradient(135deg, transparent 5%, rgba(243, 150, 59, 0.85) 15%, transparent 25%), linear-gradient(135deg, transparent 45%, rgba(243, 150, 59, 0.85) 55%, transparent 65%), linear-gradient(45deg, transparent 25%, rgba(243, 150, 59, 0.85) 35%, transparent 50%);
  background-size: 8px 2px;
  background-repeat: repeat-x;
  background-position: left bottom;
}
atom-text-editor.editor .linter-highlight.linter-warning.linter-gutter,
.linter-highlight.linter-warning.linter-gutter {
  color: rgba(243, 150, 59, 0.85);
  z-index: 0;
}
atom-text-editor.editor .linter-highlight.linter-error .region,
.linter-highlight.linter-error .region {
  background-image: linear-gradient(45deg, transparent 65%, rgba(219, 71, 71, 0.85) 80%, transparent 90%), linear-gradient(135deg, transparent 5%, rgba(219, 71, 71, 0.85) 15%, transparent 25%), linear-gradient(135deg, transparent 45%, rgba(219, 71, 71, 0.85) 55%, transparent 65%), linear-gradient(45deg, transparent 25%, rgba(219, 71, 71, 0.85) 35%, transparent 50%);
  background-size: 8px 2px;
  background-repeat: repeat-x;
  background-position: left bottom;
}
atom-text-editor.editor .linter-highlight.linter-error.linter-gutter,
.linter-highlight.linter-error.linter-gutter {
  color: rgba(219, 71, 71, 0.85);
  z-index: 1;
}
.tree-view span.name {
  position: relative;
}
.tree-view .linter-info {
  top: 0;
  left: 1.8rem;
  width: calc(100% - 1.8rem);
  height: 100%;
  position: absolute;
  background-image: linear-gradient(45deg, transparent 65%, rgba(98, 135, 173, 0.85) 80%, transparent 90%), linear-gradient(135deg, transparent 5%, rgba(98, 135, 173, 0.85) 15%, transparent 25%), linear-gradient(135deg, transparent 45%, rgba(98, 135, 173, 0.85) 55%, transparent 65%), linear-gradient(45deg, transparent 25%, rgba(98, 135, 173, 0.85) 35%, transparent 50%);
  background-size: 8px 2px;
  background-repeat: repeat-x;
  background-position: left bottom;
}
.tree-view .linter-warning {
  top: 0;
  left: 1.8rem;
  width: calc(100% - 1.8rem);
  height: 100%;
  position: absolute;
  background-image: linear-gradient(45deg, transparent 65%, rgba(243, 150, 59, 0.85) 80%, transparent 90%), linear-gradient(135deg, transparent 5%, rgba(243, 150, 59, 0.85) 15%, transparent 25%), linear-gradient(135deg, transparent 45%, rgba(243, 150, 59, 0.85) 55%, transparent 65%), linear-gradient(45deg, transparent 25%, rgba(243, 150, 59, 0.85) 35%, transparent 50%);
  background-size: 8px 2px;
  background-repeat: repeat-x;
  background-position: left bottom;
}
.tree-view .linter-error {
  top: 0;
  left: 1.8rem;
  width: calc(100% - 1.8rem);
  height: 100%;
  position: absolute;
  background-image: linear-gradient(45deg, transparent 65%, rgba(219, 71, 71, 0.85) 80%, transparent 90%), linear-gradient(135deg, transparent 5%, rgba(219, 71, 71, 0.85) 15%, transparent 25%), linear-gradient(135deg, transparent 45%, rgba(219, 71, 71, 0.85) 55%, transparent 65%), linear-gradient(45deg, transparent 25%, rgba(219, 71, 71, 0.85) 35%, transparent 50%);
  background-size: 8px 2px;
  background-repeat: repeat-x;
  background-position: left bottom;
}
.linter-gutter.icon::before {
  width: 100%;
  font-size: 1em;
}
.linter-status-count span {
  border-radius: 0px;
  padding: 4px 6px;
}
.linter-status-count span:first-child {
  border-top-left-radius: 2px;
  border-bottom-left-radius: 2px;
}
.linter-status-count span:last-child {
  border-top-right-radius: 2px;
  border-bottom-right-radius: 2px;
}
.linter-status-count.hide-config,
.linter-status-count.hide-pane {
  display: none;
}
.linter-cursor-line {
  width: 100%;
  padding-left: 0;
}
