/*
** THEME VARIABLES
*/
/* stylelint-disable */
body {
  font-size: 0.75em;
  color: #abb2bf;
  background-color: #121417;
}
atom-workspace-axis.horizontal {
  padding: 0.5em;
}
atom-workspace-axis.vertical .item-views {
  border-right: 1px solid #000000;
  border-bottom: 1px solid #000000;
  border-left: 1px solid #000000;
}
atom-panel.footer {
  border-top: 1px solid #000000;
}
atom-text-editor-minimap {
  background-color: rgba(62, 68, 81, 0.05);
}
.btn {
  border-width: 1px;
  border-style: solid;
  border-radius: 3px;
  padding: 0.5em;
  height: auto;
  width: auto;
  min-width: 5em;
  background-color: transparent;
  transition: all 0.25s ease;
  border-color: #abb2bf !important;
  color: #282c34;
  background-color: #abb2bf;
}
.btn:hover {
  color: #282c34;
  background-color: rgba(171, 178, 191, 0.8);
}
.btn:focus {
  color: #282c34;
  background-color: #abb2bf;
  outline: none;
}
.btn.selected {
  color: #abb2bf;
  background-color: transparent;
}
.btn.selected:hover {
  color: #abb2bf;
  background-color: rgba(171, 178, 191, 0.2);
}
.btn.btn-primary {
  border-color: #abb2bf !important;
  color: #282c34;
  background-color: #abb2bf;
}
.btn.btn-primary:hover {
  color: #282c34;
  background-color: rgba(171, 178, 191, 0.8);
}
.btn.btn-primary:focus {
  color: #282c34;
  background-color: #abb2bf;
  outline: none;
}
.btn.btn-primary.selected {
  color: #abb2bf;
  background-color: transparent;
}
.btn.btn-primary.selected:hover {
  color: #abb2bf;
  background-color: rgba(171, 178, 191, 0.2);
}
.btn.btn-info {
  border-color: #33a0ff !important;
  color: #282c34;
  background-color: #33a0ff;
}
.btn.btn-info:hover {
  color: #282c34;
  background-color: rgba(51, 160, 255, 0.8);
}
.btn.btn-info:focus {
  color: #282c34;
  background-color: #33a0ff;
  outline: none;
}
.btn.btn-info.selected {
  color: #33a0ff;
  background-color: transparent;
}
.btn.btn-info.selected:hover {
  color: #33a0ff;
  background-color: rgba(51, 160, 255, 0.2);
}
.btn.btn-success {
  border-color: #43d08a !important;
  color: #282c34;
  background-color: #43d08a;
}
.btn.btn-success:hover {
  color: #282c34;
  background-color: rgba(67, 208, 138, 0.8);
}
.btn.btn-success:focus {
  color: #282c34;
  background-color: #43d08a;
  outline: none;
}
.btn.btn-success.selected {
  color: #43d08a;
  background-color: transparent;
}
.btn.btn-success.selected:hover {
  color: #43d08a;
  background-color: rgba(67, 208, 138, 0.2);
}
.btn.btn-warning {
  border-color: #e0c285 !important;
  color: #282c34;
  background-color: #e0c285;
}
.btn.btn-warning:hover {
  color: #282c34;
  background-color: rgba(224, 194, 133, 0.8);
}
.btn.btn-warning:focus {
  color: #282c34;
  background-color: #e0c285;
  outline: none;
}
.btn.btn-warning.selected {
  color: #e0c285;
  background-color: transparent;
}
.btn.btn-warning.selected:hover {
  color: #e0c285;
  background-color: rgba(224, 194, 133, 0.2);
}
.btn.btn-error {
  border-color: #e05252 !important;
  color: #282c34;
  background-color: #e05252;
}
.btn.btn-error:hover {
  color: #282c34;
  background-color: rgba(224, 82, 82, 0.8);
}
.btn.btn-error:focus {
  color: #282c34;
  background-color: #e05252;
  outline: none;
}
.btn.btn-error.selected {
  color: #e05252;
  background-color: transparent;
}
.btn.btn-error.selected:hover {
  color: #e05252;
  background-color: rgba(224, 82, 82, 0.2);
}
.btn-group .btn:first-child,
.btn-group .btn:first-child:hover {
  border-left-style: solid;
  border-left-width: 1px;
}
.btn-group .btn:last-child,
.btn-group .btn:last-child:hover {
  border-right-style: solid;
  border-right-width: 1px;
}
atom-text-editor[mini] {
  border-radius: 3px;
  padding: 0.5em;
  background-color: #3e4451;
}
atom-text-editor[mini] .highlight.selection .region {
  background-color: rgba(255, 255, 255, 0.2);
}
.control-group .controls .checkbox label {
  position: relative;
}
.control-group .controls .checkbox label .input-checkbox {
  position: absolute;
  top: 0;
  right: 100%;
  margin-top: 0.5em;
  margin-right: 0.5em;
}
.control-group .form-control {
  padding: 0.5em;
  height: 2em;
  font-size: 1.25em;
  cursor: pointer;
  transition: all 0.25s ease;
}
.list-group {
  display: inline-block;
  width: 100%;
  background-color: #282c34;
}
.list-group li {
  display: block;
  border-style: solid;
  border-width: 1px;
  border-color: transparent;
  padding: 0.5em;
  font-size: 1em;
  line-height: 1.5em;
}
.list-group li::after {
  display: block;
  clear: both;
  content: " ";
}
.list-group li.two-lines .status {
  min-height: 3em;
}
.list-group li.two-lines .primary-line,
.list-group li.two-lines .secondary-line {
  padding-left: 0.5em;
}
.list-group li.selected {
  border-color: #abb2bf;
  border-radius: 3px;
  background-color: #3e4451;
}
.list-group li.selected::before {
  display: none;
}
.list-group li .status {
  float: left;
}
/*
** HIGHLIGHTS
*/
.highlight-info {
  border-radius: 3px;
  padding: 0.5em;
  color: #33a0ff;
  background-color: rgba(153, 207, 255, 0.2);
}
.highlight-success {
  border-radius: 3px;
  padding: 0.5em;
  color: #43d08a;
  background-color: rgba(149, 228, 189, 0.2);
}
.highlight-warning {
  border-radius: 3px;
  padding: 0.5em;
  color: #e0c285;
  background-color: rgba(245, 235, 214, 0.2);
}
.highlight-error {
  border-radius: 3px;
  padding: 0.5em;
  color: #e05252;
  background-color: rgba(240, 168, 168, 0.2);
}
.keystroke {
  border: 1px solid currentColor;
  border-radius: 3px;
  padding: 0.25em;
  font-size: 0.75em;
}
.info-message,
.info-messages,
.loading,
.status-renamed,
.text-info {
  color: #33a0ff !important;
}
.status-success,
.text-success {
  color: #43d08a !important;
}
.status-modified,
.text-warning {
  color: #e0c285 !important;
}
.error-message,
.error-messages,
.status-error,
.text-error {
  color: #e05252 !important;
}
a {
  color: inherit;
}
autocomplete-suggestion-list.popover-list.select-list {
  display: inline-block;
  border: 1px solid #121417;
  width: auto !important;
  min-width: 20em;
  max-width: 60em;
  font-size: 0.75em;
  background-color: #121417;
  box-shadow: 0 0 0.5em 0 rgba(0, 0, 0, 0.8);
}
autocomplete-suggestion-list.popover-list.select-list .suggestion-list-scroller .list-group {
  margin: 0;
}
autocomplete-suggestion-list.popover-list.select-list .suggestion-list-scroller .list-group li {
  display: block;
  overflow: hidden;
  padding: 0;
  height: 2em;
  line-height: 2em;
}
autocomplete-suggestion-list.popover-list.select-list .suggestion-list-scroller .list-group li::after {
  display: block;
  clear: both;
  content: " ";
}
autocomplete-suggestion-list.popover-list.select-list .suggestion-list-scroller .list-group li .icon-container {
  display: inline-block;
  float: left;
  margin-right: 0.5em;
}
autocomplete-suggestion-list.popover-list.select-list .suggestion-list-scroller .list-group li .word-container {
  display: inline;
  position: relative;
  padding: 0;
  width: 100%;
  max-width: 20em;
}
autocomplete-suggestion-list.popover-list.select-list .suggestion-list-scroller .list-group li .word-container .word {
  float: none;
}
autocomplete-suggestion-list.popover-list.select-list .suggestion-list-scroller .list-group li .word-container .word .character-match {
  font-weight: bold;
}
autocomplete-suggestion-list.popover-list.select-list .suggestion-list-scroller .list-group li .left-label,
autocomplete-suggestion-list.popover-list.select-list .suggestion-list-scroller .list-group li .right-label {
  display: inline;
  overflow: auto;
  float: none;
}
autocomplete-suggestion-list.popover-list.select-list .suggestion-list-scroller .list-group li.selected::before {
  height: 2em;
  line-height: 2em;
}
autocomplete-suggestion-list.popover-list.select-list .suggestion-description {
  font-size: 1em;
  background-color: #121417;
}
autocomplete-suggestion-list.popover-list.select-list .suggestion-description .suggestion-description-content {
  font-size: 1em;
}
autocomplete-suggestion-list.popover-list.select-list .suggestion-description .suggestion-description-more-link {
  font-size: 1em;
}
.project-find,
.find-and-replace {
  border: none;
  padding: 0;
  background-color: #121417;
}
.project-find .header,
.find-and-replace .header {
  display: flex;
  flex-direction: row;
  align-items: flex-end;
  justify-content: space-between;
  padding: 0;
}
.project-find .header .header-item,
.find-and-replace .header .header-item {
  margin: 0;
  padding: 0.25em;
}
.project-find .header .description,
.find-and-replace .header .description {
  flex: 2 1 320px;
}
.project-find .header .options-label,
.find-and-replace .header .options-label {
  display: flex;
  flex-direction: column;
  flex-wrap: wrap;
  align-items: flex-start;
  justify-content: space-between;
  flex: 1 1 160px;
}
.project-find .header .options-label .btn-group,
.find-and-replace .header .options-label .btn-group {
  width: 100%;
}
.project-find .find-container .input-block-item,
.find-and-replace .find-container .input-block-item,
.project-find .replace-container .input-block-item,
.find-and-replace .replace-container .input-block-item {
  flex: 1 1 160px;
}
.project-find .find-container .input-block-item.editor-container,
.find-and-replace .find-container .input-block-item.editor-container,
.project-find .replace-container .input-block-item.editor-container,
.find-and-replace .replace-container .input-block-item.editor-container {
  flex: 2 0 320px;
}
/* Used inside tree-view */
linter-decoration {
  left: 0 !important;
  width: 100% !important;
}
linter-decoration.linter-error {
  border-bottom: 3px double #e05252 !important;
}
linter-decoration.linter-warning {
  border-bottom: 1px dashed #e0c285 !important;
}
linter-decoration.linter-info {
  border-bottom: 1px dotted #33a0ff !important;
}
/* Used inside editor */
.linter-highlight.linter-error.linter-gutter {
  color: #e05252 !important;
}
.linter-highlight.linter-error .region {
  border-bottom: 3px double #e05252 !important;
}
.linter-highlight.linter-warning.linter-gutter {
  color: #e0c285 !important;
}
.linter-highlight.linter-warning .region {
  border-bottom: 1px dashed #e0c285 !important;
}
.linter-highlight.linter-info.linter-gutter {
  color: #33a0ff !important;
}
.linter-highlight.linter-info .region {
  border-bottom: 1px dotted #33a0ff !important;
}
/* Linter tooltips (hovering highlighted syntax) */
#linter-tooltip {
  /* stylelint-disable-line */
  margin: 0;
  border: 1px solid #121417;
  max-width: 60em;
  font-size: 0.75em;
  background-color: #121417;
  box-shadow: 0 0 0.5em 0 rgba(0, 0, 0, 0.8);
}
#linter-tooltip linter-messages linter-message {
  overflow: visible;
  clear: both;
  position: relative;
  padding: 0.5em;
}
#linter-tooltip linter-messages linter-message:last-child::before {
  border: none;
  content: none;
}
#linter-tooltip linter-messages linter-message::after {
  display: inline-block;
  position: absolute;
  top: 0.25em;
  left: 0.25em;
  font-family: "Octicons Regular";
  font-size: 2em;
  font-weight: lighter;
  line-height: 0.75em;
}
#linter-tooltip linter-messages linter-message + linter-message {
  border-top: 1px solid #121417;
}
#linter-tooltip linter-messages linter-message.error {
  color: #e05252;
  background-color: rgba(240, 168, 168, 0.2);
}
#linter-tooltip linter-messages linter-message.error::after {
  content: "\f08f";
}
#linter-tooltip linter-messages linter-message.warning {
  color: #e0c285;
  background-color: rgba(245, 235, 214, 0.2);
}
#linter-tooltip linter-messages linter-message.warning::after {
  content: "\f02c";
}
#linter-tooltip linter-messages linter-message.info {
  color: #33a0ff;
  background-color: rgba(153, 207, 255, 0.2);
}
#linter-tooltip linter-messages linter-message.info::after {
  content: "\f059";
}
#linter-tooltip linter-messages linter-message > span {
  display: block;
  padding-left: 2.5em;
}
#linter-tooltip linter-messages linter-message > span > a {
  color: inherit !important;
}
#linter-tooltip linter-messages linter-message > a {
  display: none;
}
/* @status-bar */
.linter-status-count {
  overflow: visible;
  max-width: none;
}
.linter-status-count span[class^="highlight"] {
  transition: all 0.25s ease;
}
.linter-status-count span[class^="highlight"] + span {
  margin-left: 0.5em;
}
.linter-status-count span[class^="highlight"]::before {
  margin-right: 0.25em;
}
.linter-status-count span[class^="highlight"]:nth-child(1)::before {
  content: "!";
}
.linter-status-count span[class^="highlight"]:nth-child(2)::before {
  content: "?";
}
.linter-status-count span[class^="highlight"]:nth-child(3)::before {
  content: "i";
}
atom-notifications {
  top: 5em;
}
atom-notification,
atom-notification.has-close,
atom-notification.has-close:first-child {
  box-shadow: 0 0 1em 0 #121417;
}
atom-notification.info,
atom-notification.has-close.info,
atom-notification.has-close:first-child.info {
  background-color: #282c34;
}
atom-notification.info::before,
atom-notification.has-close.info::before,
atom-notification.has-close:first-child.info::before {
  color: #ffffff !important;
  background-color: #33a0ff !important;
}
atom-notification.info .content,
atom-notification.has-close.info .content,
atom-notification.has-close:first-child.info .content {
  color: #abb2bf;
  background-color: rgba(51, 160, 255, 0.05);
}
atom-notification.info .close-all,
atom-notification.has-close.info .close-all,
atom-notification.has-close:first-child.info .close-all {
  border-color: #abb2bf !important;
  color: #abb2bf;
}
atom-notification.info .close-all:hover,
atom-notification.has-close.info .close-all:hover,
atom-notification.has-close:first-child.info .close-all:hover {
  background-color: rgba(51, 160, 255, 0.2);
}
atom-notification.success,
atom-notification.has-close.success,
atom-notification.has-close:first-child.success {
  background-color: #282c34;
}
atom-notification.success::before,
atom-notification.has-close.success::before,
atom-notification.has-close:first-child.success::before {
  color: #000000 !important;
  background-color: #43d08a !important;
}
atom-notification.success .content,
atom-notification.has-close.success .content,
atom-notification.has-close:first-child.success .content {
  color: #abb2bf;
  background-color: rgba(67, 208, 138, 0.05);
}
atom-notification.success .close-all,
atom-notification.has-close.success .close-all,
atom-notification.has-close:first-child.success .close-all {
  border-color: #abb2bf !important;
  color: #abb2bf;
}
atom-notification.success .close-all:hover,
atom-notification.has-close.success .close-all:hover,
atom-notification.has-close:first-child.success .close-all:hover {
  background-color: rgba(67, 208, 138, 0.2);
}
atom-notification.warning,
atom-notification.has-close.warning,
atom-notification.has-close:first-child.warning {
  background-color: #282c34;
}
atom-notification.warning::before,
atom-notification.has-close.warning::before,
atom-notification.has-close:first-child.warning::before {
  color: #000000 !important;
  background-color: #e0c285 !important;
}
atom-notification.warning .content,
atom-notification.has-close.warning .content,
atom-notification.has-close:first-child.warning .content {
  color: #abb2bf;
  background-color: rgba(224, 194, 133, 0.05);
}
atom-notification.warning .close-all,
atom-notification.has-close.warning .close-all,
atom-notification.has-close:first-child.warning .close-all {
  border-color: #abb2bf !important;
  color: #abb2bf;
}
atom-notification.warning .close-all:hover,
atom-notification.has-close.warning .close-all:hover,
atom-notification.has-close:first-child.warning .close-all:hover {
  background-color: rgba(224, 194, 133, 0.2);
}
atom-notification.fatal,
atom-notification.has-close.fatal,
atom-notification.has-close:first-child.fatal,
atom-notification.error,
atom-notification.has-close.error,
atom-notification.has-close:first-child.error {
  background-color: #282c34;
}
atom-notification.fatal::before,
atom-notification.has-close.fatal::before,
atom-notification.has-close:first-child.fatal::before,
atom-notification.error::before,
atom-notification.has-close.error::before,
atom-notification.has-close:first-child.error::before {
  color: #ffffff !important;
  background-color: #e05252 !important;
}
atom-notification.fatal .content,
atom-notification.has-close.fatal .content,
atom-notification.has-close:first-child.fatal .content,
atom-notification.error .content,
atom-notification.has-close.error .content,
atom-notification.has-close:first-child.error .content {
  color: #abb2bf;
  background-color: rgba(224, 82, 82, 0.05);
}
atom-notification.fatal .close-all,
atom-notification.has-close.fatal .close-all,
atom-notification.has-close:first-child.fatal .close-all,
atom-notification.error .close-all,
atom-notification.has-close.error .close-all,
atom-notification.has-close:first-child.error .close-all {
  border-color: #abb2bf !important;
  color: #abb2bf;
}
atom-notification.fatal .close-all:hover,
atom-notification.has-close.fatal .close-all:hover,
atom-notification.has-close:first-child.fatal .close-all:hover,
atom-notification.error .close-all:hover,
atom-notification.has-close.error .close-all:hover,
atom-notification.has-close:first-child.error .close-all:hover {
  background-color: rgba(224, 82, 82, 0.2);
}
atom-notification + atom-notification,
atom-notification.has-close + atom-notification,
atom-notification.has-close:first-child + atom-notification {
  margin-top: 0.25em;
}
atom-notification .close,
atom-notification.has-close .close,
atom-notification.has-close:first-child .close {
  color: #abb2bf;
}
atom-notification .content .detail,
atom-notification.has-close .content .detail,
atom-notification.has-close:first-child .content .detail {
  background-color: #282c34;
}
atom-notification .content .message,
atom-notification.has-close .content .message,
atom-notification.has-close:first-child .content .message {
  padding: 1em;
  width: auto;
}
atom-panel.modal {
  border: 1px solid #121417;
  border-radius: 3px;
  background-color: #121417;
  box-shadow: 0 0 1em 0 rgba(0, 0, 0, 0.8);
}
status-bar {
  border: none;
  font-size: inherit;
}
status-bar .status-bar-left,
status-bar .status-bar-right {
  display: flex;
  flex-direction: row;
  padding: 0.5em 0;
}
status-bar .status-bar-left .inline-block {
  margin-right: 1em;
}
status-bar .status-bar-right .inline-block {
  margin-left: 1em;
}
status-bar .file-info.buffer-modified .current-path {
  color: #33a0ff;
}
status-bar .file-info.buffer-modified::after {
  content: none;
}
status-bar busy-signal {
  opacity: 1;
  margin: 0;
  transition: opacity 0.25s ease;
}
status-bar busy-signal.loading-spinner-tiny {
  border-radius: 1em;
  background-color: #121417;
}
status-bar busy-signal.idle {
  opacity: 0;
}
.tab-bar {
  display: flex;
  justify-content: flex-start;
  border-bottom: 1px solid #000000;
  background-color: #121417;
}
.tab-bar .tab {
  display: flex;
  flex: 1 2 10em;
  flex-wrap: nowrap;
  align-items: center;
  justify-content: space-between;
  box-sizing: border-box;
  padding: 1em;
  max-height: 4em;
  color: rgba(171, 178, 191, 0.5);
  font-size: 1em;
  background-color: #121417;
  transition: all 0.25s ease;
}
.tab-bar .tab:hover {
  color: #abb2bf;
}
.tab-bar .tab:hover .close-icon {
  opacity: 1;
}
.tab-bar .tab.active {
  flex: 2 1 auto;
  bottom: -1px;
  width: 20em;
  max-width: 20em;
  color: #abb2bf;
  background-color: #282c34;
  box-shadow: 0 -1px 2px 0 #000000;
}
.tab-bar .tab.active:first-child {
  border-left: 1px solid #000000;
}
.tab-bar .tab.active:last-child {
  border-right: 1px solid #000000;
}
.tab-bar .tab.active .close-icon {
  opacity: 1;
}
.tab-bar .tab.active .foo {
  content: "bar";
}
.tab-bar .tab .foo {
  content: "bar";
}
.tab-bar .tab.modified {
  color: #33a0ff;
}
.tab-bar .tab.modified:hover .close-icon {
  color: #33a0ff;
}
.tab-bar .tab.modified:not(:hover) .close-icon {
  top: auto;
  right: auto;
