css:
/*
** THEME VARIABLES
*/
/* stylelint-disable */
#linter-tooltip {
  margin-top: 3px;
}
#linter-tooltip linter-message.error {
  color: #ffffff;
  background-color: #e05252;
}
#linter-tooltip linter-message.error:last-child::before {
  border-color: transparent transparent #e05252 #e05252;
}
#linter-tooltip linter-message.error a {
  color: #ffffff;
}
#linter-tooltip linter-message.warning {
  color: #000000;
  background-color: #e0c285;
}
#linter-tooltip linter-message.warning:last-child::before {
  border-color: transparent transparent #e0c285 #e0c285;
}
#linter-tooltip linter-message.warning a {
  color: #000000;
}
#linter-tooltip linter-message.info {
  color: #ffffff;
  background-color: #33a0ff;
}
#linter-tooltip linter-message.info:last-child::before {
  border-color: transparent transparent #33a0ff #33a0ff;
}
#linter-tooltip linter-message.info a {
  color: #ffffff;
}
#linter-tooltip linter-message.info::before {
  opacity: 0;
}
#linter-tooltip linter-message:last-child::before {
  content: "";
  position: absolute;
  top: -4px;
  left: 0;
  border: 4px solid;
}
#linter-tooltip linter-message a {
  font-weight: bold;
}
#linter-tooltip linter-message p {
  margin-bottom: 0;
}
#linter-tooltip linter-message .linter-line {
  display: block;
}
#linter-tooltip linter-message .badge {
  color: #c8ccd5;
}
linter-message {
  display: block;
  padding: 2px 5px;
}
.icon.linter-icon::before {
  font-size: 14px;
}
atom-text-editor.editor .linter-row {
  /* Take up the full allowed width */
  left: 0;
  right: 0;
  /* Align the linter dot in the middle */
  display: flex;
  align-items: center;
  justify-content: center;
}
atom-text-editor.editor .linter-highlight.linter-info .region,
.linter-highlight.linter-info .region {
  background-image: linear-gradient(45deg, transparent 65%, #99cfff 80%, transparent 90%), linear-gradient(135deg, transparent 5%, #99cfff 15%, transparent 25%), linear-gradient(135deg, transparent 45%, #99cfff 55%, transparent 65%), linear-gradient(45deg, transparent 25%, #99cfff 35%, transparent 50%);
  background-size: 8px 2px;
  background-repeat: repeat-x;
  background-position: left bottom;
}
atom-text-editor.editor .linter-highlight.linter-info.linter-gutter,
.linter-highlight.linter-info.linter-gutter {
  color: #99cfff;
  z-index: -1;
}
atom-text-editor.editor .linter-highlight.linter-warning .region,
.linter-highlight.linter-warning .region {
  background-image: linear-gradient(45deg, transparent 65%, #f5ebd6 80%, transparent 90%), linear-gradient(135deg, transparent 5%, #f5ebd6 15%, transparent 25%), linear-gradient(135deg, transparent 45%, #f5ebd6 55%, transparent 65%), linear-gradient(45deg, transparent 25%, #f5ebd6 35%, transparent 50%);
  background-size: 8px 2px;
  background-repeat: repeat-x;
  background-position: left bottom;
}
atom-text-editor.editor .linter-highlight.linter-warning.linter-gutter,
.linter-highlight.linter-warning.linter-gutter {
  color: #f5ebd6;
  z-index: 0;
}
atom-text-editor.editor .linter-highlight.linter-error .region,
.linter-highlight.linter-error .region {
  background-image: linear-gradient(45deg, transparent 65%, #f0a8a8 80%, transparent 90%), linear-gradient(135deg, transparent 5%, #f0a8a8 15%, transparent 25%), linear-gradient(135deg, transparent 45%, #f0a8a8 55%, transparent 65%), linear-gradient(45deg, transparent 25%, #f0a8a8 35%, transparent 50%);
  background-size: 8px 2px;
  background-repeat: repeat-x;
  background-position: left bottom;
}
atom-text-editor.editor .linter-highlight.linter-error.linter-gutter,
.linter-highlight.linter-error.linter-gutter {
  color: #f0a8a8;
  z-index: 1;
}
.tree-view span.name {
  position: relative;
}
.tree-view .linter-info {
  top: 0;
  left: 1.8rem;
  width: calc(100% - 1.8rem);
  height: 100%;
  position: absolute;
  background-image: linear-gradient(45deg, transparent 65%, #99cfff 80%, transparent 90%), linear-gradient(135deg, transparent 5%, #99cfff 15%, transparent 25%), linear-gradient(135deg, transparent 45%, #99cfff 55%, transparent 65%), linear-gradient(45deg, transparent 25%, #99cfff 35%, transparent 50%);
  background-size: 8px 2px;
  background-repeat: repeat-x;
  background-position: left bottom;
}
.tree-view .linter-warning {
  top: 0;
  left: 1.8rem;
  width: calc(100% - 1.8rem);
  height: 100%;
  position: absolute;
  background-image: linear-gradient(45deg, transparent 65%, #f5ebd6 80%, transparent 90%), linear-gradient(135deg, transparent 5%, #f5ebd6 15%, transparent 25%), linear-gradient(135deg, transparent 45%, #f5ebd6 55%, transparent 65%), linear-gradient(45deg, transparent 25%, #f5ebd6 35%, transparent 50%);
  background-size: 8px 2px;
  background-repeat: repeat-x;
  background-position: left bottom;
}
.tree-view .linter-error {
  top: 0;
  left: 1.8rem;
  width: calc(100% - 1.8rem);
  height: 100%;
  position: absolute;
  background-image: linear-gradient(45deg, transparent 65%, #f0a8a8 80%, transparent 90%), linear-gradient(135deg, transparent 5%, #f0a8a8 15%, transparent 25%), linear-gradient(135deg, transparent 45%, #f0a8a8 55%, transparent 65%), linear-gradient(45deg, transparent 25%, #f0a8a8 35%, transparent 50%);
  background-size: 8px 2px;
  background-repeat: repeat-x;
  background-position: left bottom;
}
.linter-gutter.icon::before {
  width: 100%;
  font-size: 1em;
}
.linter-status-count span {
  border-radius: 0px;
  padding: 4px 6px;
}
.linter-status-count span:first-child {
  border-top-left-radius: 3px;
  border-bottom-left-radius: 3px;
}
.linter-status-count span:last-child {
  border-top-right-radius: 3px;
  border-bottom-right-radius: 3px;
}
.linter-status-count.hide-config,
.linter-status-count.hide-pane {
  display: none;
}
.linter-cursor-line {
  width: 100%;
  padding-left: 0;
}
