css:
body {
  font-size: 12px;
  color: #abb2bf;
  background-color: #282c34;
}
atom-pane {
  border-right: 1px solid #282c34;
}
atom-pane:last-child {
  border-right: none;
}
.btn {
  color: #bcc2cd;
  background-color: #3e4451;
}
.btn:hover,
.btn:focus {
  color: #abb2bf;
  background-color: #596273;
}
.btn:active {
  color: rgba(171, 178, 191, 0.66);
  background-color: #3a3f4b;
}
.btn.selected {
  color: #abb2bf;
  background-color: #596273;
}
.btn:focus,
.btn:focus:active {
  outline: none;
}
.btn.btn-primary {
  color: #abb2bf;
  background-color: #0088ff;
}
.btn.btn-primary:hover,
.btn.btn-primary:focus {
  background-color: #1492ff;
}
.btn.btn-primary:active {
  background-color: #007deb;
}
.btn.btn-primary.selected {
  background-color: #1492ff;
}
.btn.btn-primary.selected:focus,
.btn.btn-primary.selected:hover {
  background-color: #299bff;
}
.btn.btn-info {
  color: #abb2bf;
  background-color: #0088ff;
}
.btn.btn-info:hover,
.btn.btn-info:focus {
  background-color: #1492ff;
}
.btn.btn-info:active {
  background-color: #007deb;
}
.btn.btn-info.selected {
  background-color: #1492ff;
}
.btn.btn-info.selected:focus,
.btn.btn-info.selected:hover {
  background-color: #299bff;
}
.btn.btn-success {
  color: #abb2bf;
  background-color: #1c9c71;
}
.btn.btn-success:hover,
.btn.btn-success:focus {
  background-color: #1fad7e;
}
.btn.btn-success:active {
  background-color: #188b65;
}
.btn.btn-success.selected {
  background-color: #1fad7e;
}
.btn.btn-success.selected:focus,
.btn.btn-success.selected:hover {
  background-color: #22bf8a;
}
.btn.btn-warning {
  color: #abb2bf;
  background-color: #cc8533;
}
.btn.btn-warning:hover,
.btn.btn-warning:focus {
  background-color: #d08e43;
}
.btn.btn-warning:active {
  background-color: #bc7a2f;
}
.btn.btn-warning.selected {
  background-color: #d08e43;
}
.btn.btn-warning.selected:focus,
.btn.btn-warning.selected:hover {
  background-color: #d49854;
}
.btn.btn-error {
  color: #abb2bf;
  background-color: #d92626;
}
.btn.btn-error:hover,
.btn.btn-error:focus {
  background-color: #dc3838;
}
.btn.btn-error:active {
  background-color: #c72323;
}
.btn.btn-error.selected {
  background-color: #dc3838;
}
.btn.btn-error.selected:focus,
.btn.btn-error.selected:hover {
  background-color: #df4949;
}
.btn-group > .btn {
  border-color: rgba(0, 0, 0, 0.2);
}
atom-text-editor[mini] {
  padding: 0;
  padding-left: 5px;
  color: #abb2bf;
  border-radius: 2px;
  border-bottom: 1px solid #545d6d;
  max-height: 36px;
  height: 36px;
}
atom-text-editor[mini].is-focused {
  border-bottom: 1px solid rgba(171, 178, 191, 0.2);
}
atom-text-editor[mini] .placeholder-text,
atom-text-editor[mini]::shadow .placeholder-text {
  color: rgba(171, 178, 191, 0.2);
}
atom-text-editor[mini] .selection .region,
atom-text-editor[mini]::shadow .selection .region {
  background-color: rgba(171, 178, 191, 0.06);
}
.find-and-replace {
  border-top: 1px solid #282c34;
}
.status {
  color: #abb2bf;
}
.status-added {
  color: #73c990;
}
.status-ignored {
  color: rgba(171, 178, 191, 0.2);
}
.status-modified {
  color: #e2c08d;
}
.status-removed {
  color: #ff6347;
}
.status-renamed {
  color: #6494ed;
}
.key-binding {
  display: inline-block;
  margin-left: 1px;
  padding: 0 5px;
  line-height: 2;
  font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", "Roboto", "Oxygen", "Ubuntu", "Cantarell", "Fira Sans", "Droid Sans", "Helvetica Neue", sans-serif;
  font-size: .8em;
  letter-spacing: 1px;
  border-radius: 2px;
  background-color: #282c34;
}
.list-group li {
  padding-left: 5px;
}
.list-group li.selected {
  color: #abb2bf;
  background-color: rgba(171, 178, 191, 0.06);
}
.list-group li.selected:before {
  display: none;
}
.list-tree .selected {
  color: #abb2bf;
}
.select-list li {
  padding: 5px 10px;
}
.select-list .status {
  float: right;
}
.select-list.popover-list {
  padding: 5px;
  border-radius: 2px;
  background-color: rgba(40, 44, 52, 0.98);
  box-shadow: 0 2px 8px 1px rgba(0, 0, 0, 0.3);
}
.select-list.popover-list .list-group {
  margin-top: 5px;
}
.select-list.popover-list .list-group li {
  padding-left: 5px;
}
.panel-heading {
  border-bottom: 1px solid #282c34;
  background-color: #31363f;
}
.inset-panel {
  background-color: #31363f;
}
.inset-panel .panel-heading {
  background-color: #3a3f4b;
}
atom-panel.modal {
  box-shadow: 0 2px 40px 1px rgba(0, 0, 0, 0.5);
  position: absolute;
  border: 1px solid rgba(0, 0, 0, 0);
  border-top: none;
  top: 0;
  left: 0;
  margin-left: 0;
  width: 100%;
  height: 100%;
  z-index: 100;
  display: flex;
  justify-content: center;
  align-items: center;
  background-color: rgba(40, 44, 52, 0.97);
  padding: 0;
}
atom-panel.modal .select-list {
  width: 600px;
}
atom-panel.modal .select-list .list-group > li {
  font-size: 13px;
  padding: 20px;
}
atom-panel.modal .select-list .two-lines .icon {
  margin-left: -22px;
}
atom-panel.modal .select-list .primary-line {
  color: rgba(171, 178, 191, 0.7);
  font-size: 13px;
  font-weight: 600;
  padding: 0 40px;
}
atom-panel.modal .select-list .secondary-line {
  color: rgba(171, 178, 191, 0.3);
  font-size: 11px;
  line-height: 12px;
  padding: 0 40px;
}
atom-panel atom-text-editor[mini] {
  min-width: 600px;
}
.pane {
  border-left: 1px solid rgba(171, 178, 191, 0.05);
}
.preview-pane .highlight-info,
.preview-pane .match {
  background-color: #282c34;
  border: 1px solid rgba(171, 178, 191, 0.2);
  margin: 3px;
}
.preview-pane .results-view {
  padding: 0;
}
.preview-pane .results-view .path {
  padding: 0 20px;
  border-top: 1px solid rgba(171, 178, 191, 0.05);
}
.preview-pane .results-view .path:first-child {
  border-top: 0;
}
.preview-pane .results-view .path::before {
  display: none;
}
.preview-pane .results-view .path .path-details {
  font-size: 13px;
  font-weight: 600;
  padding: 10px 0;
}
.preview-pane .results-view .path .matches {
  padding-bottom: 20px;
}
.preview-pane .results-view .path .matches .search-result {
  font-weight: 500;
  color: rgba(171, 178, 191, 0.5);
  line-height: 1.5;
}
.preview-pane .results-view .path .matches .search-result:hover {
  color: #abb2bf;
}
.preview-pane .results-view .path .matches .search-result::before {
  display: none;
}
.loading-spinner-large {
  display: block;
  width: 64px;
  height: 64px;
  background-image: url(images/octocat-spinner-128.gif);
  background-repeat: no-repeat;
  background-size: 64px 64px;
}
.loading-spinner-large.inline-block {
  display: inline-block;
}
.loading-spinner-medium {
  display: block;
  width: 48px;
  height: 48px;
  background-image: url(images/octocat-spinner-128.gif);
  background-repeat: no-repeat;
  background-size: 48px 48px;
}
.loading-spinner-medium.inline-block {
  display: inline-block;
}
.loading-spinner-small {
  display: block;
  width: 32px;
  height: 32px;
  background-image: url(images/octocat-spinner-128.gif);
  background-repeat: no-repeat;
  background-size: 32px 32px;
}
.loading-spinner-small.inline-block {
  display: inline-block;
}
.loading-spinner-tiny {
  display: block;
  width: 16px;
  height: 16px;
  background-image: url(images/octocat-spinner-128.gif);
  background-repeat: no-repeat;
  background-size: 16px 16px;
}
.loading-spinner-tiny.inline-block {
  display: inline-block;
}
.ui-site-1 {
  background-color: #0088ff;
}
.ui-site-2 {
  background-color: #20b684;
}
.ui-site-3 {
  background-color: #cc8533;
}
.ui-site-4 {
  background-color: #d831b0;
}
.ui-site-5 {
  background-color: #ebdd5b;
}
.status-bar {
  font-size: 12px;
  color: #abb2bf;
  border-top: 1px solid #282c34;
  background-color: #282c34;
  opacity: 0.3;
}
.status-bar:hover {
  opacity: 1;
}
.status-bar linter-bottom-tab {
  background: none;
  padding: 0 12px 0 0;
}
.status-bar .status-bar-right {
  display: none;
}
.tab-bar {
  background-color: #282c34;
  border-bottom: 1px solid #282c34;
}
.tab-bar::-webkit-scrollbar {
  display: none;
}
.tab-bar .tab {
  color: #abb2bf;
  height: 40px;
  line-height: 40px;
  font-size: 11px;
  font-weight: 700;
  background-color: #282c34;
  text-transform: uppercase;
}
.tab-bar .tab .title::before {
  font-size: 12px;
}
.tab-bar .tab.active {
  color: #abb2bf;
  background-color: #3e4451;
}
.tab-bar .tab.modified:not(:hover) .close-icon {
  top: 50%;
  right: 14px;
  margin-top: -3px;
  border-color: #6494ed;
}
.tab-bar .tab.modified:hover .close-icon {
  color: currentColor;
}
.tab-bar .tab .close-icon {
  opacity: 0;
  transform: translateY(-1px);
  transition: opacity 90ms linear;
}
.tab-bar .tab:hover .close-icon {
  opacity: 0.3;
}
.tab-bar .tab:hover .close-icon:hover {
  opacity: 1;
}
.tab-bar .tab .close-icon:hover {
  color: #abb2bf;
}
.tab-bar .tab.active .close-icon:hover {
  color: #abb2bf;
}
.tab-bar .tab.is-dragging {
  background: #121417;
  border-color: transparent;
  opacity: 0.3;
}
.tab-bar .tab.is-dragging .close-icon {
  visibility: hidden;
}
.pane .tab-bar .tab {
  background-color: #282c34;
  border-top: 1px solid #282c34;
  color: rgba(171, 178, 191, 0.2);
  transition: color 90ms linear;
}
.pane .tab-bar .tab:hover {
  color: #abb2bf;
}
.pane .tab-bar .tab:hover.modified::after {
  display: none;
}
.pane .tab-bar .tab.modified::after {
  content: '';
  width: 5px;
  height: 5px;
  position: absolute;
  right: 16px;
  top: calc(50%);
  margin-top: -2px;
  border-radius: 50%;
  background-color: #abb2bf;
}
.pane.active .tab-bar .tab.active {
  border-top-color: #abb2bf;
  color: #abb2bf;
}
.pane.active .tab-bar .tab.active:hover {
  border-top-color: #abb2bf;
}
.platform-darwin .title-bar .title {
  display: none;
}
.platform-darwin .title-bar,
.platform-darwin .tool-bar,
.platform-darwin .tool-bar button.tool-bar-btn {
  border-width: 0;
}
.platform-darwin atom-workspace atom-workspace-axis.horizontal {
  margin-top: -23px;
  background-color: #282c34;
}
.platform-darwin atom-panel-container.left {
  margin-top: 40px;
}
.platform-darwin atom-panel-container.right {
  margin-top: 40px;
}
.platform-darwin atom-workspace atom-panel-container.left:empty + atom-workspace-axis.vertical atom-pane:first-child .tab-bar {
  padding-left: 80px;
}
.text-smaller {
  font-size: smaller;
}
.text-subtle {
  color: rgba(171, 178, 191, 0.2);
}
.text-highlight {
  color: #abb2bf;
}
.text-info {
  color: #6494ed;
}
.text-success {
  color: #73c990;
}
.text-warning {
  color: #e2c08d;
}
.text-error {
  color: #ff6347;
}
.highlight {
  padding: 1px 4px;
  color: #abb2bf;
  border-radius: 2px;
  background-color: rgba(171, 178, 191, 0.06);
}
.highlight-info {
  padding: 1px 4px;
  color: #abb2bf;
  border-radius: 2px;
  background-color: #0088ff;
}
.highlight-warning {
  padding: 1px 4px;
  color: #abb2bf;
  border-radius: 2px;
  background-color: #cc8533;
}
.highlight-error {
  padding: 1px 4px;
  color: #abb2bf;
  border-radius: 2px;
  background-color: #d92626;
}
.highlight-success {
  padding: 1px 4px;
  color: #abb2bf;
  border-radius: 2px;
  background-color: #1c9c71;
}
.background-message {
  font-weight: bold;
  color: rgba(171, 178, 191, 0.2);
}
.tooltip .tooltip-inner {
  line-height: 1;
  padding: .75em;
  white-space: nowrap;
  max-width: none;
}
.tooltip .keystroke {
  padding: .15em .4em;
  margin: 0 -0.3em 0 0.25em;
  border-radius: 2px;
  color: #abb2bf;
  background: rgba(171, 178, 191, 0.06);
}
.tree-view-resizer {
  border-right: 1px solid #282c34;
}
.tree-view {
  color: rgba(171, 178, 191, 0.4);
  background-color: #282c34;
  opacity: 0.6;
  padding: 20px;
  transition: opacity 90ms linear;
}
.tree-view:hover {
  opacity: 1;
}
.tree-view .list-item:hover {
  color: rgba(171, 178, 191, 0.9);
}
.tree-view .list-item.selected {
  color: rgba(171, 178, 191, 0.9);
}
.tree-view .list-item.header {
  color: rgba(171, 178, 191, 0.7);
  font-weight: 700;
}
.atom-text-editor .line[class*="highlight-line-multi-line-solid-bottom"],
:host .line[class*="highlight-line-multi-line-solid-bottom"],
atom-text-editor .line[class*="highlight-line-multi-line-solid-bottom"],
atom-text-editor::shadow .line[class*="highlight-line-multi-line-solid-bottom"] {
  background-color: rgba(171, 178, 191, 0.05);
  border-bottom-color: rgba(171, 178, 191, 0.2);
}
atom-text-editor .indent-guide {
  color: rgba(171, 178, 191, 0.1);
}
atom-text-editor::shadow .gutter {
  background-color: #282c34;
  color: rgba(99, 109, 131, 0.2);
}
atom-text-editor::shadow .gutter .cursor-line {
  background-color: rgba(171, 178, 191, 0.05);
  box-shadow: 0 1px 0 rgba(171, 178, 191, 0.2);
}
.pane {
  opacity: 0.6;
  transition: opacity 90ms linear;
}
.pane.active,
.pane:hover {
  opacity: 1;
}
.scrollbars-visible-always ::-webkit-scrollbar {
  width: 10px;
  height: 10px;
}
.scrollbars-visible-always ::-webkit-scrollbar-track,
.scrollbars-visible-always ::-webkit-scrollbar-corner {
  background: #222425;
}
.scrollbars-visible-always ::-webkit-scrollbar-thumb {
  background: rgba(92, 92, 92, 0.5);
  border-radius: 5px;
  box-shadow: 0 0 1px black inset;
}
