atom-workspace {
  font-family: Roboto, Helvetica, Arial, sans-serif;
}
atom-pane-axis.vertical > atom-pane:not(:first-child) {
  border-top: 1px solid #181a1c;
}
atom-pane-axis.horizontal > atom-pane:not(:first-child) {
  border-left: 1px solid #181a1c;
}
.list-group .two-lines .secondary-line {
  color: rgba(197, 200, 198, 0.3);
}
.list-group li {
  padding: 10px;
}
.list-group li.selected {
  position: relative;
  background: #35393c;
}
.list-group li.selected::before {
  content: none;
}
.status-bar {
  border-top: 1px solid #181a1c;
  height: 30px;
  font-weight: 300;
  letter-spacing: .4px;
}
.status-bar .flexbox-repaint-hack {
  padding: 0 10px;
  align-items: center;
}
.tab-bar {
  background-color: #1d1f21;
  border-bottom: 1px solid #181a1c;
}
.tab-bar .tab {
  font-weight: 300;
  letter-spacing: .4px;
  font-size: 12px;
  height: 48px;
  line-height: 48px;
  padding: 0 10px;
  min-width: 160px;
  max-width: 200px;
  cursor: pointer;
  transition: box-shadow 500ms cubic-bezier(0.4, 0, 0.2, 1);
  box-shadow: inset 0 -2px 0 0 transparent;
}
.tab-bar .tab.active {
  box-shadow: inset 0 -2px 0 0 #3896fe;
}
.tab-bar .tab .close-icon {
  width: 8px;
  height: 100%;
  display: flex;
  align-items: center;
  position: absolute;
  top: 0;
  right: 10px;
  cursor: pointer;
}
.tab-bar .tab .close-icon:hover {
  color: rgba(197, 200, 198, 0.9);
}
.tab-bar .tab .close-icon::before {
  opacity: 0;
  position: absolute;
  transition: opacity 250ms cubic-bezier(0.4, 0, 0.2, 1), color 250ms cubic-bezier(0.4, 0, 0.2, 1);
}
.tab-bar .tab .close-icon::after {
  position: absolute;
  display: block;
  content: '';
  opacity: 0;
  transition: opacity 250ms cubic-bezier(0.4, 0, 0.2, 1), color 250ms cubic-bezier(0.4, 0, 0.2, 1);
}
.tab-bar .tab:hover .close-icon::before {
  opacity: 1;
}
.tab-bar .tab.modified:not(:hover) .close-icon {
  width: 8px;
  height: 100%;
  display: flex;
  align-items: center;
  position: absolute;
  top: 0;
  right: 10px;
  border: none;
}
.tab-bar .tab.modified:not(:hover) .close-icon::before {
  display: block;
}
.tab-bar .tab.modified:not(:hover) .close-icon::after {
  content: '';
  width: 8px;
  height: 8px;
  border: 2px solid #3896fe;
  border-radius: 50%;
  opacity: 1;
}
.tab-bar .tab.modified:hover .close-icon {
  color: currentColor;
}
.tab-bar .tab.modified:hover .close-icon::after {
  content: '';
  width: 8px;
  height: 8px;
  border: 2px solid #3896fe;
  border-radius: 50%;
  opacity: 0;
}
.tab-bar .tab.modified:hover .close-icon:hover {
  color: rgba(197, 200, 198, 0.9);
}
.tab-bar .tab.is-dragging {
  background: #131516;
  opacity: .5;
}
.tab-bar .tab.is-dragging .close-icon {
  visibility: hidden;
}
.tree-view-resizer {
  border-right: 1px solid #181a1c;
}
.tree-view {
  background-color: #1d1f21;
  font-weight: 300;
  letter-spacing: .4px;
}
.tree-view .list-tree {
  color: rgba(197, 200, 198, 0.8);
}
.tree-view .list-item.selected {
  color: #ffffff;
}
.tree-view .entry::before {
  height: 40px;
}
.tree-view .entry.selected::before {
  background: #3896fe;
}
.tree-view .entry.selected > .list-item {
  color: #ffffff;
}
.tree-view .list-item {
  height: 40px;
  display: flex;
  align-items: center;
}
.tree-view .project-root::before {
  height: 48px;
}
.tree-view .project-root > .header {
  height: 48px;
  display: flex;
  align-items: center;
}
atom-panel.modal.from-top {
  top: 75px;
}
atom-panel.modal {
  background: transparent;
}
atom-panel.modal::before {
  content: '';
  position: absolute;
  top: 0;
  right: 0;
  bottom: 0;
  left: 0;
  background: #1d1f21;
  z-index: -1;
  border-radius: 3px;
  box-shadow: 0 0 10px 0px rgba(0, 0, 0, 0.25);
}
atom-panel.modal::after {
  content: '';
  position: fixed;
  background: #050606;
  opacity: 0.75;
  top: 0;
  right: 0;
  bottom: 0;
  left: 0;
  backface-visibility: hidden;
  z-index: -2;
}
atom-panel.modal .list-group {
  border: 1px solid #111213;
}
atom-panel.modal .list-group li:not(:last-child) {
  border-bottom: 1px solid #111213;
}
atom-panel.modal .list-group li .icon.status {
  float: right;
}
atom-text-editor[mini] {
  overflow: auto;
  padding: 0 10px;
  border-radius: 3px;
  border: 1px solid #111213;
  background-color: #131516;
}
atom-pane .tab-bar .tab .title[data-path]:before {
  margin-right: 5px;
}
