css:
.busy {
  display: inline-block;
  padding-top: 1px;
}
.busy span.icon-gear {
  text-align: center;
  color: #eceded;
}
.busy span.icon-gear.is-busy::before {
  animation: spin 1.5s linear infinite;
  color: #6494ed;
}
@-webkit-keyframes spin {
  0% {
    transform: rotate(0deg);
  }
  100% {
    transform: rotate(360deg);
  }
}
.spin::before {
  animation: spin 1.5s linear infinite;
  text-align: center;
}
